Leveling Castle:
local NormalRewards = require(script.NormalRewards)
local HardRewards = require(script.HardRewards)

local DropsInfo = setmetatable({}, {
	__index = function(_, FloorIndex: number)
		return function(Difficulty: string)
			local RegularDrops = NormalRewards[FloorIndex]
			local ExtraDrops = {}

			if Difficulty == "Hard" then
				ExtraDrops = HardRewards[FloorIndex]
			end

			local DropsData = {
				Items = {}
			}

			for Name, Amount in RegularDrops do
				DropsData.Items[Name] = {
					Chance = 100,
					Amount = {
						[Amount] = 100,
					}
				}
			end

			for Name, Amount in ExtraDrops do
				DropsData.Items[Name] = {
					Chance = 100,
					Amount = {
						[Amount] = 100,
					}
				}
			end

			return DropsData
		end
	end,
})

return DropsInfo

