Leveling Castle:
local ReplicatedStorage = game:GetService("ReplicatedStorage")

local MapDatas = script.Parent
local LevelingCastleAutoGeneratedFloors = require(ReplicatedStorage.Shared.Info.LevelingCastleAutoGeneratedFloors)

local function FetchFloorDataByKey(FloorIndex: number, Key: string): any
	local RoomData = LevelingCastleAutoGeneratedFloors[FloorIndex]
	if not RoomData then return end

	local MapName = RoomData.Map
	local MapAct = RoomData.Act

	local MapModule = MapDatas:FindFirstChild(MapName)
	if not MapModule then return end

	return require(MapModule)[Key][MapAct]
end

