local Data = {
	DiamondsPerFloor = 250,
	DiamondsPerHardFloor = 500,
	DiamondsPerBossFloor = 25000,

	Acts = setmetatable({}, {
		__index = function(_, FloorIndex: number)
			return FetchFloorDataByKey(FloorIndex, "Acts")
		end,
	}),

	Difficulty = setmetatable({}, {
		__index = function(_, FloorIndex: number)
			return FetchFloorDataByKey(FloorIndex, "Difficulty")
		end,
	}),

	BossModels = setmetatable({
		[0] = ReplicatedStorage.Assets.Enemies["Leveling Castle"]["Cerberus"],
		[50] = ReplicatedStorage.Assets.Enemies["Leveling Castle"]["Vulcan"],
		[75] = ReplicatedStorage.Assets.Enemies["Leveling Castle"]["Metus"],
		[100] = ReplicatedStorage.Assets.Enemies["Leveling Castle"]["Demon King"],
	}, {
		__index = function(_, FloorIndex: number)
			if FloorIndex > 100 then
				local Remainder = FloorIndex % 100

				if Remainder == 25 then
					return ReplicatedStorage.Assets.Enemies["Leveling Castle"]["Cerberus"]
				elseif Remainder == 50 then
