			if FloorIndex > 100 then
				local Remainder = FloorIndex % 100

				if Remainder == 25 then
					return ReplicatedStorage.Assets.Animations["Cerberus"].Idle
				elseif Remainder == 50 then
					return ReplicatedStorage.Assets.Animations["Vulcan"].Idle
				elseif Remainder == 75 then
