			end

			return FetchFloorDataByKey(FloorIndex, "Offsets")
		end,
	}),

	Images = setmetatable({}, {
		__index = function(_, FloorIndex: number)
			local RNG = Random.new(FloorIndex)
			local Variant = RNG:NextInteger(1, #FloorVariantImages)
			return FloorVariantImages[Variant]
		end,
	}),
