local ReplicatedStorage = game:GetService("ReplicatedStorage")
local Players = game:GetService("Players")
local RunService = game:GetService("RunService")
local TypeList = require(ReplicatedStorage.Shared.Info.TypeList)
local SpeedLibrary = require(ReplicatedStorage.Shared.Core.Speed)
local TaskLibrary = SpeedLibrary.TaskLibrary

return {
	CanTrade = true, DisplayName = "Gobanks",
	Rarity = "Mythic",
	PlaceType = "Ground",
	Type = TypeList.Fighting,
	PlacementLimit = 1,

	Passives = {
		OnSpawn = function(Unit, SummonHandler)
			if not RunService:IsServer() then return end
			local function summonGhosts()
				while Unit and Unit.Parent do
					local Info = Unit:FindFirstChild("Info")
					local Stats = Info and Info:FindFirstChild("Stats")
					local Damage = Stats and Stats:FindFirstChild("Damage") and Stats.Damage.Value or 0

					local GhostHealth = Damage * 5

					local OwnerName = Info and Info:FindFirstChild("Owner") and Info.Owner.Value
					local Owner = OwnerName and Players:FindFirstChild(OwnerName)
					if Owner and SummonHandler then
						SummonHandler.Create("Ghost", GhostHealth, Unit, Owner)
					end

					TaskLibrary.wait(30)
					if not Unit or not Unit.Parent then break end
				end
			end

			task.spawn(summonGhosts)
		end,
	},

	PassivesData = {
