local ReplicatedStorage = game:GetService("ReplicatedStorage")
local ServerStorage = game:GetService("ServerStorage")
local Players = game:GetService("Players")

local TypeList = require(ReplicatedStorage.Shared.Info.TypeList)

local KILLS_FOR_PASSIVE = 35

local keywords = {
	"Auto", "Upgrade", "kills"
}

local function formatDescription(description)
	for _, keyword in ipairs(keywords) do
		description = description:gsub(keyword, '<font color="rgb(255, 0, 0)">' .. keyword .. '</font>')
	end
	return description
end

return {
	CanTrade = true, DisplayName = "Virtual Sniper",
	Rarity = "Secret",
	PlaceType = "Hill",
	Type = TypeList.Water,

	RequirementType = "Kills",
	KillsForPassive = 35, 

	Passives = {
		OnKill = function(EnemyClass, Unit)
			local Network = require(ReplicatedStorage.Shared.Network.GeneralNetwork)			
			local Units = require(ServerStorage.Server.Gameplay.Unit)

			if not Unit:GetAttribute("KillsForPassive") then
				Unit:SetAttribute("KillsForPassive", 0)
			end

			Unit:SetAttribute("KillsForPassive", Unit:GetAttribute("KillsForPassive") + 1)

			if Unit:FindFirstChild("Info") then
				local Owner = Unit:FindFirstChild("Info"):FindFirstChild("Owner")
				local PlayerName = Owner and Owner.Value
				local Player = PlayerName and Players:FindFirstChild(PlayerName)

				if Player and Unit:GetAttribute("KillsForPassive") >= 20 then
					Unit:SetAttribute("KillsForPassive", Unit:GetAttribute("KillsForPassive") - 20)
					Network.VFX:Server():FireAll("Unit", "Upgrade", Unit:GetPivot().Position)

					Units.Upgrade(Player, Unit, true)
				end
			end
		end,
	},

	PassivesData = {
		[1] = {
