local ServerStorage = game:GetService("ServerStorage")
local ReplicatedStorage = game:GetService("ReplicatedStorage")
local RunService = game:GetService("RunService")

local TypeList = require(ReplicatedStorage.Shared.Info.TypeList)

return {
	CanTrade = true, DisplayName = "Kaillou (Serious)",
	Rarity = "Secret",
	PlaceType = "Ground",
	Type = TypeList.Fighting,
	PlacementLimit = 1,

	Passives = {
		OnPlaced = function(ThisUnit: Model)
			if not RunService:IsServer() then
				return
			end

			if ThisUnit:HasTag("PrimeableSaitama") then
				return
			end

			local Info = ThisUnit:FindFirstChild("Info") :: Folder
			local Buffs = Info and Info:FindFirstChild("Buffs") :: Folder

			local Methods = require(ServerStorage.Server.Utility.Methods)
			local DamageBuff = Buffs and Buffs:FindFirstChild("Damage") :: NumberValue

			if DamageBuff then
				DamageBuff.Value -= 0.5
			else
				local DamageBuff = Instance.new("NumberValue")
				DamageBuff.Name = "Damage"
				DamageBuff.Value = -0.5
				DamageBuff.Parent = Buffs
			end

			ThisUnit:AddTag("PrimeableSaitama")
			Methods.RecalculateStats(ThisUnit)
		end,
	},

	PassivesData = {
		[1] = {
			Title = "Locking In",
			Description = "This unit starts with -50% damage. If any Gen Z unit is sold or destroyed, gain +20% damage and remove the -50% damage nerf. This passive's stat amount is increased by +20% more damage for every upgrade the Gen Z had gained before being sold/deleted. <i>Can only apply once</i>",
