local Players = game:GetService("Players")
local ReplicatedStorage = game:GetService("ReplicatedStorage")
local ServerStorage = game:GetService("ServerStorage")

local TypeList = require(ReplicatedStorage.Shared.Info.TypeList)

return {
	CanTrade = true, DisplayName = "Juno",
	Rarity = "Legendary",
	PlaceType = "Ground",
	Type = TypeList.Holy,
	PlacementLimit = 1,

	Passives = {
		OnPlaced = function(Unit: Model)
			Unit:SetAttribute("AutoUpgrades", true)
		end,

		OnWaveChanged = function(_, Unit: Model)
			local Network = require(ReplicatedStorage.Shared.Network.GeneralNetwork)			
			local Units = require(ServerStorage.Server.Gameplay.Unit)

			local UnitInfo = Unit:FindFirstChild("Info")
			local Owner = UnitInfo and UnitInfo:FindFirstChild("Owner")
			local PlayerName = Owner and Owner.Value
			local Player = PlayerName and Players:FindFirstChild(PlayerName)

			if Player then
				Network.VFX:Server():FireAll("Unit", "Upgrade", Unit:GetPivot().Position)
				Units.Upgrade(Player, Unit, true)
			end
		end,
	},

