tor values:FindFirstChild(\"MatchWave\")\nend\n\nlocal function getUpgradeLevel(info: Instance): number\n\tlocal up = info:FindFirstChild(\"Upgrade\")\n\treturn (up and up.Value) or 0\nend\n\nlocal function getPlaceWave(info: Instance): number\n\tlocal pw = info:FindFirstChild(\"PlacedWave\")\n\treturn (pw and pw.Value) or 0\nend\n\nlocal function getPerWaveGain(): number\n\treturn 5\nend\n\nreturn {\n\tCanTrade = true,\n\tDisplayName = \"Gun [Pact]\",\n\tRarity = \"Mythic\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Fighting,\n\tPlacementLimit = 1,\n\n\tPassives = {\n\t\tOnSpawn = function(UnitModel: Model)\n\t\t\tif not RunService:IsServer() then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\tlocal srcGuid = UnitModel:GetAttribute(\"UnitGUID\") or tostring(os.clock())\n\t\t\tlocal marker = BUFF_ID .. \"_\" .. tostring(srcGuid)\n\n\t\t\ttask.spawn(function()\n\t\t\t\tlocal function getPassiveRange()\n\t\t\t\t\tlocal info = UnitModel:FindFirstChild(\"Info\")\n\t\t\t\t\tlocal stats = info and info:FindFirstChild(\"Stats\")\n\t\t\t\t\tlocal rangeVal = stats and stats:FindFirstChild(\"Range\")\n\t\t\t\t\treturn (rangeVal and rangeVal.Value) or 0\n\t\t\t\tend\n\n\t\t\t\tlocal function getUnitPos()\n\t\t\t\t\tlocal hrp = UnitModel:FindFirstChild(\"HumanoidRootPart\")\n\t\t\t\t\treturn hrp and hrp.Position\n\t\t\t\tend\n\n\t\t\t\tlocal function collectAlliesInRange()\n\t\t\t\t\tlocal origin = getUnitPos()\n\t\t\t\t\tif not origin then return {} end\n\n\t\t\t\t\tlocal range = getPassiveRange()\n\t\t\t\t\tif range <= 0 then return {} end\n\n\t\t\t\t\tlocal allies = {}\n\t\t\t\t\tfor _, targetModel in ipairs(workspace.Units:GetChildren()) do\n\t\t\t\t\t\tif targetModel ~= UnitModel then\n\t\t\t\t\t\t\tlocal thrp = targetModel:FindFirstChild(\"HumanoidRootPart\")\n\t\t\t\t\t\t\tif thrp then\n\t\t\t\t\t\t\t\tlocal dist = (origin - thrp.Position).Magnitude\n\t\t\t\t\t\t\t\tif dist <= range then\n\t\t\t\t\t\t\t\t\tt