Name = \"Gogetable (Angel)\",\n\tRarity = \"Mythic\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Holy,\n\tPlacementLimit = 1,\n\t\n\tTagTeamWith = \"Vegito\",\n\tTagTeamRange = 30,\n\tTagTeamType = \"FusionRivals\",\n\tTagTeamData = {\n\t\tTitle = \"Fusion Rivals\",\n\t\tDescription = \"When near Vegitu, each kill Gogetable scores grants Vegitu +10% damage for 5s (stacks). Vegitu's kills also buff Gogetable.\",\n\t\tKillBuff = {\n\t\t\tStat = \"Damage\",\n\t\t\tAmount = 10,\n\t\t\tDuration = 5,\n\t\t},\n\t},\n\t\n\tPassives = {\n\t\tOnSpawn = function(UnitModel: Model)\n\t\t\tif not RunService:IsServer() then return end\n\t\t\tif not UnitModel or not UnitModel.Parent then return end\n\n\t\t\tlocal info = UnitModel:FindFirstChild(\"Info\")\n\t\t\tlocal buffs = info and info:FindFirstChild(\"Buffs\")\n\t\t\tif not buffs then\n\t\t\t\twarn(\"[FusionDuration] Missing Info/Buffs on\", UnitModel.Name)\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tif UnitModel:GetAttribute(\"FusionTimerStarted\") then\n\t\t\t\treturn\n\t\t\tend\n\t\t\tUnitModel:SetAttribute(\"FusionTimerStarted\", true)\n\n\t\t\tlocal dmgBuff = getOrCreateNumber(buffs, \"Damage\")\n\t\t\tlocal rangeBuff = getOrCreateNumber(buffs, \"Range\")\n\n\t\t\tlocal elapsed = 0\n\t\t\tlocal lastDmgMult = 0\n\t\t\tlocal appliedRange = false\n\t\t\t\n\t\t\tlocal function getGameSpeed(): number\n\t\t\t\treturn GameSpeed and GameSpeed.Value or 1\n\t\t\tend\n\n\t\t\tlocal function applyDmgFromSecondsLeft(secondsLeft: number)\n\t\t\t\tlocal wantedMult = secondsLeft / 100\n\n\t\t\t\tdmgBuff.Value -= lastDmgMult\n\t\t\t\tdmgBuff.Value += wantedMult\n\t\t\t\tlastDmgMult = wantedMult\n\t\t\tend\n\n\t\t\tlocal function applyPermanentRangeOnce()\n\t\t\t\tif appliedRange then return end\n\t\t\t\tappliedRange = true\n\n\t\t\t\trangeBuff.Value += (RANGE_PERM_BUFF / 100)\n\t\t\tend\n\n\t\t\ttask.spawn(function()\n\t\t\t\twhile UnitModel and UnitModel.Parent do\n\t\t\t\t\tlocal speed = getGameSpeed()\n\t\t\t\t\telapsed += TICK * speed\n\t\t