eRange()\n\t\t\t\t\tlocal info = UnitModel:FindFirstChild(\"Info\")\n\t\t\t\t\tlocal stats = info and info:FindFirstChild(\"Stats\")\n\t\t\t\t\tlocal rangeVal = stats and stats:FindFirstChild(\"Range\")\n\t\t\t\t\treturn (rangeVal and rangeVal.Value) or 0\n\t\t\t\tend\n\n\t\t\t\tlocal function getUnitPos()\n\t\t\t\t\tlocal hrp = UnitModel:FindFirstChild(\"HumanoidRootPart\")\n\t\t\t\t\treturn hrp and hrp.Position\n\t\t\t\tend\n\n\t\t\t\tlocal function collectAlliesInRange()\n\t\t\t\t\tlocal origin = getUnitPos()\n\t\t\t\t\tif not origin then return {} end\n\n\t\t\t\t\tlocal range = getPassiveRange()\n\t\t\t\t\tif range <= 0 then return {} end\n\n\t\t\t\t\tlocal allies = {}\n\t\t\t\t\tfor _, targetModel in ipairs(workspace.Units:GetChildren()) do\n\t\t\t\t\t\tif targetModel ~= UnitModel then\n\t\t\t\t\t\t\tlocal thrp = targetModel:FindFirstChild(\"HumanoidRootPart\")\n\t\t\t\t\t\t\tif thrp then\n\t\t\t\t\t\t\t\tlocal dist = (origin - thrp.Position).Magnitude\n\t\t\t\t\t\t\t\tif dist <= range then\n\t\t\t\t\t\t\t\t\ttable.insert(allies, targetModel)\n\t\t\t\t\t\t\t\tend\n\t\t\t\t\t\t\tend\n\t\t\t\t\t\tend\n\t\t\t\t\tend\n\t\t\t\t\treturn allies\n\t\t\t\tend\n\n\t\t\t\tlocal allies = collectAlliesInRange()\n\t\t\t\t\n\t\t\t\twhile UnitModel and UnitModel.Parent == workspace.Units do\n\t\t\t\t\tfor _, targetModel in ipairs(allies) do\n\t\t\t\t\t\tif targetModel == UnitModel then\n\t\t\t\t\t\t\tcontinue\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\tif getType(targetModel) ~= TARGET_ELEMENT then\n\t\t\t\t\t\t\tcontinue\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\tif targetModel:GetAttribute(marker) then\n\t\t\t\t\t\t\tcontinue\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\tlocal applied = Methods.NewBuff(UnitModel, { Damage = BUFF_PERCENT }, BUFF_ID, false, targetModel)\n\n\t\t\t\t\t\tif applied and applied[targetModel] then\n\t\t\t\t\t\t\ttargetModel:SetAttribute(marker, true)\n\t\t\t\t\t\tend\n\t\t\t\t\tend\n\n\t\t\t\t\tTaskLibrary.wait(TICK)\n\t\t\t\tend\n\t\t\tend)\n\n\t\t\tUnitModel.Destroying:Once(function()\n\t\t\t\tMethods.NewBuffRemoveAll(UnitModel, true)\n\t\t\tend)\n\t\tend,\n\n\t\tOnSell = function(ThisUnit: Model)\n\t\t\tif not RunService:IsServer() then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\t\t\tMethods.NewBuffRemoveAll(ThisUnit, true)\n\t\tend,\n\t},\n\n\tPassivesData = {\n\t\t[1] = {\n\t\t\tTitle = \"Godspeed\",\n\t\t\tDescription = \"Every electric element unit in this unit's range will gain a 25% damage buff (units can only be affected by this passive once).\",\n\t\t},\n\t},\n\n\t[0] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 7, StatusList = {\"Paralysis\"},\n\t\tCost = 500, Damage = 350, Range = 20, SPA = 1,\n\t},\n\t\n\t[1] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 7, StatusList = {\"Paralysis\"},\n\t\tCost = 1000, Damage = 700, Range = 22, SPA = 1,\n\t},\n\t\n\t[2] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Lightning Funnel\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 12, StatusList = {\"Paralysis\"},\n\t\tCost = 2000, Damage = 600, Range = 24, SPA = 0.5,\n\t},\n\t\n\t[3] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Lightning Funnel\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 12, StatusList = {\"Paralysis\"},\n\t\tCost = 3000, Damage = 900, Range = 26, SPA = 0.5,\n\t},\n\t\n\t[4] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Vortex\", Ability = \"\", AOEType = \"Full\", AOESize = Vector3.one * 14, StatusList = {\"Paralysis\"},\n\t\tCost = 5000, Damage = 4500, Range = 28, SPA = 1.5,\n\t},\n\n\t[5] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Vortex\", Ability = \"\", AOEType = \"Full\", AOESize = Vector3.one * 14, StatusList = {\"Paralysis\"},\n\t\tCost = 12000, Damage = 9000, Range = 30, SPA = 1.5,\n\t},\n\n\t[6] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModule