Rand:NextInteger(1, #AvailableUnits)\n\t\t\tlocal UnitSelected = AvailableUnits[UnitIndex]\n\t\t\tlocal Demon = AvailableDemons[Rand:NextInteger(1, #AvailableDemons)]\n\n\t\t\twhile UnitSelected.Name == \"Cid\" do -- Don't let cid get pot/cowardly demon!\n\t\t\t\tif Demon ~= \"Pot Demon\" and Demon ~= \"Cowardly Demon\" then\n\t\t\t\t\tbreak\n\t\t\t\tend\n\n\t\t\t\tlocal CidableDemons = {}\n\n\t\t\t\tfor _, Demon2 in AvailableDemons do\n\t\t\t\t\tif Demon2 ~= \"Pot Demon\" and Demon2 ~= \"Cowardly Demon\" then\n\t\t\t\t\t\ttable.insert(CidableDemons, Demon2)\n\t\t\t\t\tend\n\t\t\t\tend\n\n\t\t\t\tif #CidableDemons == 0 then\n\t\t\t\t\tif #AvailableUnits == 1 then -- only cid was in range\n\t\t\t\t\t\treturn\n\t\t\t\t\tend\n\n\t\t\t\t\ttable.remove(AvailableUnits, UnitIndex)\n\n\t\t\t\t\tUnitIndex = Rand:NextInteger(1, #AvailableUnits)\n\t\t\t\t\tUnitSelected = AvailableUnits[UnitIndex]\n\n\t\t\t\t\tcontinue\n\t\t\t\telse\n\t\t\t\t\tDemon = CidableDemons[Rand:NextInteger(1, #CidableDemons)]\n\t\t\t\t\tbreak\n\t\t\t\tend\n\t\t\tend\n\n\t\t\tlocal UnitInfo = UnitSelected:FindFirstChild(\"Info\")\n\t\t\tlocal UnitStats = UnitInfo and UnitInfo:FindFirstChild(\"Stats\")\n\n\t\t\tif UnitStats and UnitStats:FindFirstChild(\"Money\") then -- Farm units should only get pot demon!\n\t\t\t\twhile UnitStats:FindFirstChild(\"Money\") do\n\t\t\t\t\tif Demon == \"Pot Demon\" then\n\t\t\t\t\t\tbreak\n\t\t\t\t\tend\n\n\t\t\t\t\tlocal Index = table.find(AvailableDemons, \"Pot Demon\")\n\n\t\t\t\t\tif not Index then\n\t\t\t\t\t\tif #AvailableUnits == 1 then\n\t\t\t\t\t\t\treturn\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\ttable.remove(AvailableUnits, UnitIndex)\n\n\t\t\t\t\t\tUnitIndex = Rand:NextInteger(1, #AvailableUnits)\n\t\t\t\t\t\tUnitSelected = AvailableUnits[UnitIndex]\n\n\t\t\t\t\t\tUnitInfo = UnitSelected:FindFirstChild(\"Info\")\n\t\t\t\t\t\tUnitStats = UnitInfo:FindFirstChild(\"Stats\")\n\n\t\t\t\t\t\tcontinue\n\t\t\t\t\tend\n\n\t\t\t\t\tDemon = \"Pot Demon\"\n\t\t\t\t\tbreak\n\t\t\t\t