,\n\n\t[5] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Final Slash\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 14, StatusList = {},\n\t\tCost = 50000, Damage = 180000, Range = 65, SPA = 8,\n\t}\n}\n","ZBroly":"local ReplicatedStorage = game:GetService(\"ReplicatedStorage\")\nlocal RunService = game:GetService(\"RunService\")\nlocal ServerStorage = game:GetService(\"ServerStorage\")\nlocal Values = ReplicatedStorage.Values\nlocal AllSpawn = Values.AllSpawn\n\nlocal SpeedLibrary = require(ReplicatedStorage.Shared.Core.Speed)\nlocal TaskLibrary = SpeedLibrary.TaskLibrary\nlocal TypeList = require(ReplicatedStorage.Shared.Info.TypeList)\n\nlocal Event = ReplicatedStorage.EventsAndFunctions.RemoteEvents.QuickTimeEvent\n\nlocal keywords = {\n\t\"60\", \"500\", \"3\", \"10\", \"berserk\", \"spawn\", \"QTE\", \"Fail\", \"LSSJ\",\"4\", \"SSJ\", \"Base Form\", \"SSJ Form\", \"LSSJ Form\", \"Immune\", \"Overflowing Power\"\n}\n\nlocal function formatDescription(description)\n\tfor _, keyword in ipairs(keywords) do\n\t\tdescription = description:gsub(keyword, '<font color=\"rgb(0, 255, 0)\">' .. keyword .. '</font>')\n\tend\n\treturn description\nend\n\nlocal function randomPercent(tables)\n\tlocal randomNumber = Random.new():NextNumber(1, 100)\n\tlocal correspondingProbability = 0\n\n\tfor key, probability in pairs(tables) do\n\t\tlocal previousProbability = correspondingProbability\n\t\tcorrespondingProbability += probability\n\n\t\tif previousProbability < randomNumber and randomNumber <= correspondingProbability then\n\t\t\treturn key\n\t\tend\n\tend\t\nend\n\nlocal berserk_percent = {\n\tberserk_active = 5,\n}\n\nlocal Keybind_Tables = {\n\t\"E\", \"R\", \"T\", \"Y\",\n}\n\nlocal function Start_QuickTimeEvent(Player , Unit , ...)\n\tif not Player:FindFirstChild(\"QuickTimeEvent\") then\n\t\tlocal QTEVENT_FOLDER = Instance.new(\"Folder\")\n\t\tQTEVENT_FOLDER.Name = \"QuickTimeEvent\"\n\n\t\tlocal GENARATE_NUMBER = math.random(3,5)\n\n\t\trepeat \n\t\t\tlocal KEYBIND_G = math.random(1 , #Keybind_Tables)\n\n\t\t\tif QTEVENT_FOLDER:FindFirstChild(KEYBIND_G) == nil then\n\t\t\t\tlocal KEY = Keybind_Tables[KEYBIND_G]\n\t\t\t\tlocal INT = Instance.new(\"StringValue\")\n\t\t\t\tINT.Value = KEY\n\t\t\t\tINT.Name = #QTEVENT_FOLDER:GetChildren() + 1\n\t\t\t\tINT.Parent = QTEVENT_FOLDER\n\n\t\t\t\tif INT.Name == \"1\" then\n\t\t\t\t\tINT:SetAttribute(\"ACTIVE\" , true)\n\t\t\t\tend\n\t\t\tend\n\n\t\tuntil #QTEVENT_FOLDER:GetChildren() == GENARATE_NUMBER\n\n\t\tQTEVENT_FOLDER.Parent = Player\n\n\t\tif not Player.PlayerGui:FindFirstChild(\"QuickTimeEvent\") then\n\t\t\tlocal GUI = ReplicatedStorage.Assets.UI.QuickTimeEvent.QuickTimeEvent:Clone()\n\t\t\tGUI.Unit.Value = Unit\n\t\t\tGUI.Parent = Player.PlayerGui\n\t\tend\n\tend\nend\n\nlocal function Start_QuickTimeEvent2(Player, Unit, ...)\n\tif not Player:FindFirstChild(\"QuickTimeEvent\") then\n\t\tlocal QTEVENT_FOLDER = Instance.new(\"Folder\")\n\t\tQTEVENT_FOLDER.Name = \"QuickTimeEvent\"\n\n\t\t-- Create a list of available keys\n\t\tlocal availableKeys = {}\n\t\tfor i, key in ipairs(Keybind_Tables) do\n\t\t\ttable.insert(availableKeys, key)\n\t\tend\n\n\t\t-- Randomly select keys from the list without duplicates\n\t\tlocal GENARATE_NUMBER = math.random(3, 5)\n\t\tfor i = 1, GENARATE_NUMBER do\n\t\t\tlocal index = math.random(1, #availableKeys)\n\t\t\tlocal selectedKey = availableKeys[index]\n\n\t\t\t-- Create StringValue for the selected key\n\t\t\tlocal INT = Instance.new(\"StringValue\")\n\t\t\tINT.Value = selectedKey\n\t\t\tINT.Name = tostring(i)\n\t\t\tINT.Parent = QTEVENT_FOLDER\n\n\t\t\t-- Mark the first key as active\n\t\t\tif INT.Name == \"1\" then\n\t\t\t\tINT:SetAttribute(\"ACTIVE\", true)\n\t\t\tend\n\n\t\t\t-- Remove the selected key from the list\n\t\t\ttable.remove(availableKeys, index)\n\t\tend\n\n\t\tQTEVENT_FOLDER.Parent = Player\n\n\t\t-- Clone GUI if it doesn't exist\n\t\tif not Player.PlayerGui:FindFirstChild(\"QuickTimeEvent\") then\n\t\t\tlocal GUI = ReplicatedStorage.Assets.UI.QuickTimeEvent.QuickTimeEvent:Clone()\n\t\t\tGUI.Unit.Value = Unit\n\t\t\tGUI.Parent = Player.PlayerGui\n\t\tend\n\tend\nend\n\nlocal abilitykeywords = {\n\t\"LSSJ\",\n}\n\nlocal function formatDescriptionAbility(description)\n\tfor _, keyword in ipairs(abilitykeywords) do\n\t\tdescription = description:gsub(keyword, '<font color=\"rgb(0, 200, 255)\">' .. keyword .. '</font>')\n\tend\n\treturn description\nend\n\nlocal function Unit_Upgrade(Unit)\n\tlocal Player = game.Players[Unit.Info.Owner.Value]\n\n\tif Unit.Info.Upgrade.Value == 0 then\n\t\trequire(ServerStorage.Server.Gameplay.Unit).Upgrade(Player, Unit, true)\n\tend\nend\n\nlocal function UnitTransform(Unit , Form)\n\trequire(ServerStorage.Server.Attacks[script.Name].BrolyTransform)(Unit , Form)\nend\n\nreturn {\n\tCanTrade = true, DisplayName = \"Broccoli\",\n\tRarity = \"Secret\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Fighting,\n\tPlacementLimit = 1,\n\n\tPassiveName = \"Slumbering Rage\",\n\tPassiveDescriptions = {\n\t\tBase = \"Base Form: Cannot attack, 5% chance to go berserk when enemies spawn (QTE will appear). Fail QTE: Transforms into LSSJ. After 4 waves: Transforms into SSJ automatically.\",\n\t\tSSJ = \"SSJ Form: Gains +3% DMG every 1 Attack. After 500 seconds goes into his LSSJ form.\",\n\t\tLSSJ = \"LSSJ Form: Immune to all negative status effects. Every 60 seconds this unit does a special attack (Overflowing Power). Retains the attack boost from his previous form.\",\n\t},\n\t\n\tPassivesData = {\n\t\t[1] = {\n\t\t\tTitle = \"Base\",\n\t\t\tDescription = \"Cannot attack, 5% chance to go berserk when enemies spawn (QTE will appear). Fail QTE: Transforms into LSSJ. After 4 waves: Transforms into SSJ automatically.\",\n\t\t},\n\t\t[2] = {\n\t\t\tTitle = \"SSJ\",\n\t\t\tDescription = \"Gains +3% DMG every 1 Attack. After 500 seconds goes into his LSSJ form.\",\n\t\t},\n\t\t[3] = {\n\t\t\tTitle = \"LSSJ\",\n\t\t\tDescription = \"Immune to all negative status effects. Every 60 seconds this unit does a special attack (Overflowing Power). Retains the attack boost from his previous form.\",\n\t\t},\n\t},\n\t\n\tRequirementType = \"\",\n\tformatDescription = formatDescription,\n\n\tPassives = {\n\n\t\tIgnoreStatus = function(Unit)\n\t\t\tif Unit:GetAttribute(\"Form\") == \"LSSJ\" then\n\t\t\t\treturn true\n\t\t\tend\n\t\tend,\n\n\t\tOnPlaced = function(Unit)\n\t\t\tUnit:SetAttribute(\"IgnoreAttack\" , true)\n\t\tend,\n\n\t\tOnAttack = function(Player , Unit)\n\t\t\tif Unit:FindFirstChild(\"Info\")  then\t\n\t\t\t\tlocal Info = Unit:FindFirstChild(\"Info\")\n\n\t\t\t\tif Unit:GetAttribute(\"Form\") == \"SSJ\" then\n\n\t\t\t\t\tif Info:FindFirstChild(\"Buffs\") then\n\t\t\t\t\t\tlocal Buffs = Unit.Info.Buffs\n\n\t\t\t\t\t\tif Buffs:FindFirstChild(\"Damage\") then\n\t\t\t\t\t\t\tBuffs:FindFirstChild(\"Damage\").Value += 0.03\n\t\t\t\t\t\telse\n\t\t\t\t\t\t\tlocal INT_DMG = Instance.new(\"NumberValue\" , Buffs)\n\t\t\t\t\t\t\tINT_DMG.Name = \"Damage\"\n\t\t\t\t\t\t\tINT_DMG.Value = 0.03\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\tlocal StatCalculator = require(ReplicatedStorage.Shared.Core.StatCalculator)\n\t\t\t\t\t\tlocal DataManager = require(ServerStorage.Server.Data.DataManager)\n\t\t\t\t\t\tlocal Data = DataManager.GetData(Player)\n\t\t\t\t\t\tlocal DamageBuffs = StatCalculator.GetFullStats(Data.Units[Info.ID.Value], Info.Upgrade.Value, Buffs).Damage\n\t\t\t\t\t\tlocal NextCalculatedStats = StatCalculator.GetFullStats(Data.Units[Info.ID.Value], Info.Upgrade.Value + 1, Buffs)\n\t\t\t\t\t\tlocal NextStats = Info.NextStats\n\n\t\t\t\t\t\tfor StatName, StatValue in pairs(NextCalculatedStats) do\n\t\t\t\t\t\t\tlocal StatObject = NextStats:FindFirstChild(StatName) \n\t\t\t\t\t\t\tif StatObject then\n\t\t\t\t\t\t\t\tStatObject.Value = StatValue\n\t\t\t\t\t\t\tend\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\tInfo.Stats.Damage.Value = DamageBuffs\n\n\t\t\t\t\tend\n\t\t\t\tend\n\n\t\t\tend\n\t\tend,\n\n\t\tOnWaveChanged = function(EnemyClass, Unit)\n\t\t