ice\")\nlocal ReplicatedStorage = game:GetService(\"ReplicatedStorage\")\nlocal RunService = game:GetService(\"RunService\")\n\nlocal TypeList = require(ReplicatedStorage.Shared.Info.TypeList)\n\nlocal AttacksPerPassive = 10\nlocal TotalUpgradesCap = 100 -- Muzan and Meruem don't respect this variable!\n\nreturn {\n\tCanTrade = true, DisplayName = \"Gen Z\",\n\tRarity = \"Secret\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Fire,\n\tPlacementLimit = 4,\n\n\tPassives = {\n\t\tOnAttack = function(Player: Player, ThisUnit: Model)\n\t\t\tlocal UnitInfo = ThisUnit:FindFirstChild(\"Info\")\n\t\t\tif not UnitInfo then return end\n\n\t\t\tlocal UnitBuffs = UnitInfo:FindFirstChild(\"Buffs\")\n\t\t\tif not UnitBuffs then return end\n\n\t\t\tlocal Attacks = ThisUnit:GetAttribute(\"OverdriveAttacks\") or 0\n\t\t\tlocal NewAttacks = (Attacks + 1) % (AttacksPerPassive + 1)\n\t\t\tThisUnit:SetAttribute(\"OverdriveAttacks\", NewAttacks)\n\n\t\t\tlocal UnitData = require(script.Parent) -- recursive requires!\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\t\t\tlocal DamageBuff = UnitBuffs and UnitBuffs:FindFirstChild(\"Damage\")\n\n\t\t\tlocal Upgrade = UnitInfo and UnitInfo:FindFirstChild(\"Upgrade\")\n\n\t\t\tif NewAttacks == AttacksPerPassive then\n\t\t\t\tif DamageBuff then\n\t\t\t\t\tDamageBuff.Value += 6\n\t\t\t\telse\n\t\t\t\t\tlocal DamageBuff = Instance.new(\"NumberValue\")\n\t\t\t\t\tDamageBuff.Name = \"Damage\"\n\t\t\t\t\tDamageBuff.Value = 6\n\t\t\t\t\tDamageBuff.Parent = UnitBuffs\n\t\t\t\tend\n\n\t\t\t\tMethods.RecalculateStats(ThisUnit)\n\t\t\telseif NewAttacks == 0 then\n\t\t\t\tif Upgrade then\n\t\t\t\t\tUpgrade.Value = math.max(0, Upgrade.Value - 1)\n\t\t\t\t\tReplicatedStorage.EventsAndFunctions.RemoteEvents.SetAOE:FireAllClients(ThisUnit, UnitData[ThisUnit.Name][Upgrade.Value].AOEType)\n\t\t\t\tend\n\n\t\t\t\tif DamageBuff then\n\t\t\t\t\tDamageBuff.Value -= 6\n\n\t\t\t\t\tif math.abs(DamageBuff.Value) < 0.0001 then\n\t\t\t\t\t\tDamageBu