anTouch = false\n\tring.CastShadow = false\n\tring.Material = Enum.Material.Neon\n\tring.Transparency = 0.6\n\tring.Shape = Enum.PartType.Cylinder\n\tring.Parent = unitModel\n\tring.Color = Color3.fromRGB(255, 0, 0)\n\n\tlocal ROT = CFrame.Angles(0, 0, math.rad(90))\n\t\n\tlocal unitPos = unitModel:GetPivot().Position\n\tlocal range = rangeVal.Value\n\tlocal r = range * 0.25\n\n\tring.Size = Vector3.new(0.15, r * 2, r * 2)\n\tring.CFrame = CFrame.new(unitPos.X, unitPos.Y - 1.25, unitPos.Z) * ROT\n\t\n\treturn ring\nend\n\nlocal function changeRingSize(unitModel: Model, ring: Part)\n\tlocal info = unitModel:WaitForChild(\"Info\")\n\tlocal stats = info:WaitForChild(\"Stats\")\n\tlocal rangeVal = stats:WaitForChild(\"Range\")\n\t\n\tlocal range = rangeVal.Value\n\tlocal r = range * 0.25\n\t\n\t\n\tring.Size = Vector3.new(0.15, r * 2, r * 2)\nend\n\nreturn {\n\tCanTrade = true, DisplayName = \"Nezu [Demon]\",\n\tRarity = \"Mythic\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Dark,\n\tPlacementLimit = 3,\n\t\n\tPassives = {\n\t\tOnSpawn = function(ThisUnit: Model)\t\t\t\n\t\t\tif not RunService:IsServer() then return end\n\t\t\tif not ThisUnit or not ThisUnit.Parent then return end\n\n\t\t\tlocal info = ThisUnit:FindFirstChild(\"Info\")\n\t\t\tlocal stats = info and info:FindFirstChild(\"Stats\")\n\t\t\tlocal rangeVal = stats and stats:FindFirstChild(\"Range\")\n\t\t\tif not rangeVal then return end\n\t\t\t\n\t\t\tlocal ring = attachRangeRing(ThisUnit)\n\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\tlocal function tickBloodLegacy()\n\t\t\t\tlocal unitPos = ThisUnit:GetPivot().Position\n\t\t\t\tlocal range = rangeVal.Value\n\t\t\t\tlocal near25 = range * 0.25\n\n\t\t\t\tlocal anyWithin25 = false\n\t\t\t\tlocal inRangeEnemies = {}\n\n\t\t\t\tlocal enemyList = Methods.GetEnemyList()\n\n\t\t\t\tfor _, enemy in pairs(enemyList) do\n\t\t\t\t\tlocal enemyPos = Vector3.new(enemy.X, enemy.Y, enemy.Z)\n\t\t\t\t\tlocal dist = (unitPos - enemyPos).Magnitude\n\n\t\t\t\t\tif dist <= range then\n\t\t\t\t\t\ttable.insert(inRangeEnemies, enemy)\n\t\t\t\t\tend\n\t\t\t\t\tif dist <= near25 then\n\t\t\t\t\t\tanyWithin25 = true\n\t\t\t\t\tend\n\t\t\t\tend\n\n\t\t\t\tlocal stacksToApply = anyWithin25 and 2 or 1\n\n\t\t\t\tfor _, enemy in ipairs(inRangeEnemies) do\n\t\t\t\t\tfor _ = 1, stacksToApply do\n\t\t\t\t\t\tif enemy then continue end\n\t\t\t\t\t\tenemy:ApplyEffect(nil, ThisUnit, \"Bloodloss\")\n\t\t\t\t\tend\n\t\t\t\tend\n\t\t\tend\n\n\t\t\ttask.spawn(function()\n\t\t\t\twhile ThisUnit and ThisUnit.Parent do\n\t\t\t\t\ttickBloodLegacy()\n\t\t\t\t\tTaskLibrary.wait(2)\n\t\t\t\tend\n\t\t\tend)\n\t\t\t\n\t\t\trangeVal.Changed:Connect(function()\n\t\t\t\tchangeRingSize(ThisUnit, ring)\n\t\t\tend)\n\t\tend,\n\t},\n\t\n\tPassivesData = {\n\t\t[1] = {\n\t\t\tTitle = \"Blood Legacy\",\n\t\t\tDescription = \"When no enemies are within 10% of this unit's range, apply 1 stack of bloodloss to every enemy in range every 2 seconds. If there are enemies within 10% of her range, apply 2 stacks of bloodloss to every enemy in range every 2 second.\",\n\t\t},\n\t},\n\n\t[0] = {\n\t\tTargetType = \"Ground\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 8, StatusList = {\"Bloodloss\"},\n\t\tCost = 1000, Damage = 3000, Range = 25, SPA = 5,\n\t},\n\n\t[1] = {\n\t\tTargetType = \"Ground\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 8, StatusList = {\"Bloodloss\"},\n\t\tCost = 1900, Damage = 5700, Range = 27, SPA = 5,\n\t},\n\n\t[2] = {\n\t\tTargetType = \"Ground\",\n\t\tModuleName = \"Attack2\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 10, StatusList = {\"Bloodloss\"},\n\t\tCost = 2700, Damage = 8000, Range = 34, SPA = 6,\n\t},\n\n\t[3] = {\n\t\tTargetType = \"Ground\",\n\t\tModuleName = \"Attack2\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 10, StatusList = {\"Bloodloss\"},\n\t\tCost = 4100, Damage