 = 4600, Damage = 9000, Range = 28, SPA = 7,\n\t},\n\t\n\t[3] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Crazy Cutter\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 15, StatusList = {\"Poison\"},\n\t\tCost = 9200, Damage = 18000, Range = 35, SPA = 7,\n\t},\n\t\n\t[4] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Bloodrush\", Ability = \"\", AOEType = \"Cone\", AOESize = Vector3.one * 45, StatusList = {\"Poison\"},\n\t\tCost = 14000, Damage = 24000, Range = 39, SPA = 6,\n\t},\n\t\n\t[5] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Bloodrush\", Ability = \"\", AOEType = \"Cone\", AOESize = Vector3.one * 45, StatusList = {\"Poison\"},\n\t\tCost = 18000, Damage = 31000, Range = 43, SPA = 6,\n\t},\n}\n","YoruichiEvo":"local ReplicatedStorage = game:GetService(\"ReplicatedStorage\")\nlocal RunService = game:GetService(\"RunService\")\nlocal ServerStorage = game:GetService(\"ServerStorage\")\nlocal Players = game:GetService(\"Players\")\n\nlocal SpeedLibrary = require(ReplicatedStorage.Shared.Core.Speed)\nlocal TaskLibrary = SpeedLibrary.TaskLibrary\n\nlocal TypeList = require(ReplicatedStorage.Shared.Info.TypeList)\n\nlocal CHARGING_MAX = 10\n\nlocal NUKE_MULT = 10\n\nlocal SPA_STEP = -0.05\nlocal SPA_CAP  = -0.30\n\nlocal function getInfoStatsBuffs(unit: Model)\n\tlocal info = unit:FindFirstChild(\"Info\")\n\tlocal stats = info and info:FindFirstChild(\"Stats\")\n\tlocal buffs = info and info:FindFirstChild(\"Buffs\")\n\treturn info, stats, buffs\nend\n\nlocal function ensureNumberValue(parent: Instance, name: string, default: number)\n\tlocal v = parent:FindFirstChild(name)\n\tif not v then\n\t\tv = Instance.new(\"NumberValue\")\n\t\tv.Name = name\n\t\tv.Value = default\n\t\tv.Parent = parent\n\tend\n\treturn v\nend\n\nlocal function hasStatus(enemy, statusName: string): boolean\n\tif not enemy or not enemy.StatusList then return false end\n\treturn table.find(enemy.StatusList, statusName) ~= nil\nend\n\nlocal function getOwnerPlayer(thisUnit: Model): Player?\n\tlocal info = thisUnit:FindFirstChild(\"Info\")\n\tlocal owner = info and info:FindFirstChild(\"Owner\")\n\tif owner and owner.Value then\n\t\treturn Players:FindFirstChild(owner.Value)\n\tend\n\treturn nil\nend\n\nlocal function inRange(thisUnit: Model, enemy, range: number): boolean\n\tif not enemy then return false end\n\tlocal unitPos = thisUnit:GetPivot().Position\n\tlocal enemyPos = Vector3.new(enemy.X, enemy.Y, enemy.Z)\n\treturn (unitPos - enemyPos).Magnitude <= range\nend\n\nlocal function applyPermanentSpaPenalty(thisUnit: Model, buffs: Folder, methods)\n\tlocal current = thisUnit:GetAttribute(\"Shunko_SpaPenalty\") or 0\n\tlocal newPenalty = math.max(SPA_CAP, current + SPA_STEP)\n\tif newPenalty == current then return end\n\n\tlocal delta = newPenalty - current\n\tthisUnit:SetAttribute(\"Shunko_SpaPenalty\", newPenalty)\n\n\tlocal spaBuff = buffs:FindFirstChild(\"SPA\")\n\tif not spaBuff then\n\t\tspaBuff = Instance.new(\"NumberValue\")\n\t\tspaBuff.Name = \"SPA\"\n\t\tspaBuff.Value = 0\n\t\tspaBuff.Parent = buffs\n\tend\n\n\tspaBuff.Value += delta\n\tmethods.RecalculateStats(thisUnit)\nend\n\nlocal function getAttackIndex(thisUnit: Model): number\n\tlocal info = thisUnit:FindFirstChild(\"Info\")\n\tlocal atk = info and info:FindFirstChild(\"AttackUpgrade\")\n\tlocal upg = info and info:FindFirstChild(\"Upgrade\")\n\n\treturn (atk and atk.Value) or (upg and upg.Value) or 0\nend\n\nlocal function findEnemyById(enemyList, id: number)\n\tif not enemyList or id == nil then return nil end\n\tfor _, e in pairs(enemyList) do\n\t\tif e and e.ID == id then\n\t\t\treturn e\n\t\tend\n\tend\n\treturn nil\nend\n\n\nreturn {\n\tCanTrade = true, DisplayName = \"Yoru [Raging Raijin]\",\n\tRarity = \"Mythic\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Electric,\n\tPlacementLimit = 2,\n\n\tPassives = {\n\t\tOnSpawn = function(ThisUnit: Model)\n\t\t\tif not RunService:IsServer() then return end\n\t\t\tif not ThisUnit or not ThisUnit.Parent then return end\n\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\tlocal info, stats, buffs = getInfoStatsBuffs(ThisUnit)\n\t\t\tif not info or not stats then\n\t\t\t\twarn(\"[Shunko] Missing Info/Stats on\", ThisUnit.Name)\n\t\t\t\treturn\n\t\t\tend\n\t\t\tif not buffs then\n\t\t\t\tbuffs = Instance.new(\"Folder\")\n\t\t\t\tbuffs.Name = \"Buffs\"\n\t\t\t\tbuffs.Parent = info\n\t\t\tend\n\n\t\t\tThisUnit:SetAttribute(\"Shunko_Charging\", 0)\n\t\t\tThisUnit:SetAttribute(\"Shunko_LastAttackTime\", os.clock())\n\t\t\tThisUnit:SetAttribute(\"Shunko_SpaPenalty\", ThisUnit:GetAttribute(\"Shunko_SpaPenalty\") or 0)\n\t\tend,\n\n\t\tOnAttack = function(player: Player, ThisUnit: Model)\n\t\t\tif not RunService:IsServer() then return end\n\t\t\tif not ThisUnit or not ThisUnit.Parent then return end\n\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\t\t\tlocal EnemyHandler = require(ServerStorage.Server.Gameplay.EnemyHandler)\n\n\t\t\tlocal info, stats, buffs = getInfoStatsBuffs(ThisUnit)\n\t\t\tif not info or not stats or not buffs then return end\n\n\t\t\tlocal dmgVal = stats:FindFirstChild(\"Damage\")\n\t\t\tlocal rangeVal = stats:FindFirstChild(\"Range\")\n\t\t\tif not dmgVal or not rangeVal then return end\n\n\t\t\tlocal attackIndex = getAttackIndex(ThisUnit)\n\t\t\tlocal targetId = Methods.GetTargetID(ThisUnit, attackIndex)\n\t\t\tif not targetId then return end\n\n\t\t\tlocal enemy = findEnemyById(Methods.GetEnemyList(), targetId)\n\t\t\tif not enemy or not enemy.StatusList then return end\n\n\t\t\tif not (hasStatus(enemy, \"Paralysis\") and hasStatus(enemy, \"Burn\")) then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tThisUnit:SetAttribute(\"Shunko_LastAttackTime\", os.clock())\n\n\t\t\tlocal stacks = (ThisUnit:GetAttribute(\"Shunko_Charging\") or 0) + 1\n\t\t\tThisUnit:SetAttribute(\"Shunko_Charging\", stacks)\n\n\t\t\tlocal ownerPlayer = getOwnerPlayer(ThisUnit) or player\n\n\t\t\tif stacks >= CHARGING_MAX then\n\t\t\t