eList.Dark,\n\tPlacementLimit = 2,\n\t\n\tTagTeamWith = \"IgrisEvo\",\n\tTagTeamRange = 20,\n\tTagTeamType = \"LoyalKnight\",\n\tTagTeamRole = \"SJW\",\n\tTagTeamData = {\n\t\tTitle = \"Loyal Knight\",\n\t\tDescription = \"Aura Farmer [Purple] serves his Monarch with unwavering loyalty.\",\n\t\tMarkDuration = 5,\n\t\tDamageBonus = 1.25,\n\t},\n\n\tPassives = {\n\t\tOnUpgrade = function(ThisUnit: Model)\n\t\t\tlocal ServerStorage = game:GetService(\"ServerStorage\")\n\t\t\tlocal Pets = require(ServerStorage.Server.Gameplay.Pets)\n\n\t\t\tlocal UnitInfo = ThisUnit:FindFirstChild(\"Info\") :: Folder\n\t\t\tlocal UnitUpgrade = UnitInfo and UnitInfo:FindFirstChild(\"Upgrade\") :: IntValue\n\t\t\tlocal ShadowPetName = UnitUpgrade and UpgradeShadowPets[UnitUpgrade.Value] :: string?\n\n\t\t\tif ShadowPetName then\n\t\t\t\tPets.GivePet(ThisUnit, ShadowPetName)\n\t\t\tend\n\t\tend,\n\n\t\tOnKill = function(Enemy: any, ThisUnit: Model, Extra: any)\n\t\t\tif Extra and Extra.KillBySummon then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tif Enemy.Class ~= \"Boss\" and math.random(1, 3) == 1 then -- Necromancy summons\n\t\t\t\tlocal ServerStorage = game:GetService(\"ServerStorage\")\n\n\t\t\t\tlocal SummonHandler = require(ServerStorage.Server.Gameplay.SummonHandler)\n\n\t\t\t\tlocal UnitInfo = ThisUnit:FindFirstChild(\"Info\")\n\t\t\t\tlocal UnitStats = UnitInfo:FindFirstChild(\"Stats\")\n\t\t\t\tlocal UnitOwner = UnitInfo:FindFirstChild(\"Owner\")\n\t\t\t\tlocal Player = Players:FindFirstChild(UnitOwner.Value)\n\n\t\t\t\tlocal BaseEnemyHealth = UnitStats.Damage.Value * 1.25\n\t\t\t\tlocal ClassHealthMultiplier = Enemy.Class.Health_Multiplier or 1\n\t\t\t\tlocal TotalEnemyHealth = BaseEnemyHealth * ClassHealthMultiplier\n\n\t\t\t\tSummonHandler.QueueFromEnemy(Enemy, TotalEnemyHealth, ThisUnit, Player, true)\n\t\t\tend\n\n\t\t\tif not ThisUnit:HasTag(\"LUK_Capped\") then -- Level Up buffs\n\t\t\t\tlocal PassiveKills = ThisUnit:GetAttribute(\"LUK\") or 0 -- Level Up Kills\n\t\t\t\tlocal NewKills = PassiveKills + 1\n\n\t\t\t\tif NewKills >= LevelUpKillsRequirement then\n\t\t\t\t\tNewKills = math.max(0, NewKills - LevelUpKillsRequirement)\n\n\t\t\t\t\tpcall(function()\n\t\t\t\t\t\tlocal ServerStorage = game:GetService(\"ServerStorage\")\n\t\t\t\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\t\t\t\tlocal BuffsAvailable = table.clone(LevelUpBuffs)\n\t\t\t\t\t\tlocal BuffsUsed = {\n\t\t\t\t\t\t\tDamage = ThisUnit:GetAttribute(\"LUK_Damage\") or 0,\n\t\t\t\t\t\t\tRange = ThisUnit:GetAttribute(\"LUK_Range\") or 0,\n\t\t\t\t\t\t\tSPA = ThisUnit:GetAttribute(\"LUK_SPA\") or 0,\n\t\t\t\t\t\t}\n\n\t\t\t\t\t\tfor Index = #BuffsAvailable, 1, -1 do\n\t\t\t\t\t\t\tlocal BuffInfo = BuffsAvailable[Index]\n\t\t\t\t\t\t\tlocal Remaining = 0\n\n\t\t\t\t\t\t\tfor Name, Value in BuffInfo do\n\t\t\t\t\t\t\t\tlocal Used = math.abs(BuffsUsed[Name])\n\t\t\t\t\t\t\t\tlocal Cap = math.abs(LevelUpBuffCaps[Name])\n\n\t\t\t\t\t\t\t\tif Used >= Cap then\n\t\t\t\t\t\t\t\t\tBuffInfo[Name] = nil\n\t\t\t\t\t\t\t\telse\n\t\t\t\t\t\t\t\t\tBuffInfo[Name] = math.min(Cap - Used, math.abs(Value)) * math.sign(Value)\n\t\t\t\t\t\t\t\t\tRemaining += 1\n\t\t\t\t\t\t\t\tend\n\t\t\t\t\t\t\tend\n\n\t\t\t\t\t\t\tif Remaining == 0 then\n\t\t\t\t\t\t\t\tBuffsAvailable[Index] = nil\n\t\t\t\t\t\t\tend\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\tif #BuffsAvailable == 0 then\n\t\t\t\t\t\t\tThisUnit:AddTag(\"LUK_Capped\")\n\t\t\t\t\t\t\treturn\n\t\t\t\t\t\tend\n\n\t\t\t\t\t\tlocal RNG = Random.new(os.clock())\n\t\t\t\t\t\tlocal BuffIndex = RNG:NextInteger(1, #LevelUpBuffs)\n\t\t\t\t\t\tlocal BuffInfo = BuffsAvailable[BuffIndex]\n\n\t\t\t\t\t\tlocal UnitInfo = ThisUnit:FindFirstChild(\"Info\")\n\t\t\t\t\t\tlocal UnitBuffs = UnitInfo:FindFirstChild(\"Buffs\")\n\n\t\t\t\t\t\tfor Name, Value in BuffInfo do\n\t\t\t\t\t\t\tlocal BuffValue = UnitBuffs:FindFirstChild(Name)\n\n\t\t\t\t\t\t\tif not BuffValue then\n\t\t\t\t\t\t\t\tBuffValue = Instance.new(\"NumberValue\")\n\t\t\t\t\t\t\t\tBuffValue.Nam