nue end\n\t\tset[child.Name] = true\n\tend\n\treturn set\nend\n\nreturn {\n\tDisplayName = \"Ichigoat [True]\",\n\tRarity = \"Royalty\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Dark,\n\tPlacementLimit = 1,\n\n\tPassives = {\n\t\tOnSpawn = function(ThisUnit: Model)\n\t\t\tif not RunService:IsServer() then return end\n\t\t\tif not ThisUnit or not ThisUnit.Parent then return end\n\n\t\t\tlocal PassiveCooldowns = require(ServerStorage.Server.Gameplay.UnitPassiveCooldowns)\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\tlocal _, stats, buffs = getInfoStatsBuffs(ThisUnit)\n\t\t\tif not stats or not buffs then\n\t\t\t\twarn(\"[EmbodimentOfLight] Missing Stats/Buffs on\", ThisUnit.Name)\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tif not ThisUnit:GetAttribute(\"EmbodimentStarted\") then\n\t\t\t\tThisUnit:SetAttribute(\"EmbodimentStarted\", true)\n\n\t\t\t\tlocal dmgBuff = buffs:FindFirstChild(\"Damage\")\n\t\t\t\tif not dmgBuff then\n\t\t\t\t\tdmgBuff = Instance.new(\"NumberValue\")\n\t\t\t\t\tdmgBuff.Name = \"Damage\"\n\t\t\t\t\tdmgBuff.Value = 0\n\t\t\t\t\tdmgBuff.Parent = buffs\n\t\t\t\tend\n\n\t\t\t\tlocal lastApplied = 0\n\n\t\t\t\tlocal function recomputeEmbodiment()\n\t\t\t\t\tif not ThisUnit or not ThisUnit.Parent then return end\n\n\t\t\t\t\tlocal rangeVal = stats:FindFirstChild(\"Range\")\n\t\t\t\t\tif not rangeVal then return end\n\n\t\t\t\t\tlocal myPos = ThisUnit:GetPivot().Position\n\t\t\t\t\tlocal r = rangeVal.Value\n\n\t\t\t\t\tlocal holyCount = 0\n\t\t\t\t\tfor _, other in ipairs(workspace.Units:GetChildren()) do\n\t\t\t\t\t\tif other ~= ThisUnit and other.Parent then\n\t\t\t\t\t\t\tif getUnitType(other) == TARGET_TYPE then\n\t\t\t\t\t\t\t\tlocal dist = (myPos - other:GetPivot().Position).Magnitude\n\t\t\t\t\t\t\t\tif dist <= r then\n\t\t\t\t\t\t\t\t\tholyCount += 1\n\t\t\t\t\t\t\t\tend\n\t\t\t\t\t\t\tend\n\t\t\t\t\t\tend\n\t\t\t\t\tend\n\n\t\t\t\t\tlocal desired = (holyCount * HOLY_PER_UNIT) / 100\n\n\t\t\t\t\tif desired ~= lastA