\"DICEROLL_ROLL\"\nlocal TAG_UPG = \"DICEROLL_UPG\"\n\nlocal function getInfo(unit: Model)\n\treturn unit and unit:FindFirstChild(\"Info\")\nend\n\nlocal function getStats(unit: Model)\n\tlocal info = getInfo(unit)\n\treturn info and info:FindFirstChild(\"Stats\")\nend\n\nlocal ATTACK_UPG_ATTR = \"DiceRoll_AttackUpgrade\"\n\nlocal function getAttackUpgrade(unit: Model): number\n\tlocal v = unit:GetAttribute(ATTACK_UPG_ATTR)\n\tif typeof(v) ~= \"number\" then\n\t\treturn 0\n\tend\n\treturn math.clamp(math.floor(v), 0, MaxAttackLevel)\nend\n\nlocal function setAttackUpgrade(unit: Model, upg: number)\n\tunit:SetAttribute(ATTACK_UPG_ATTR, math.clamp(math.floor(tonumber(upg) or 0), 0, MaxAttackLevel))\nend\n\nlocal function ensureIntValue(parent: Instance, name: string, defaultValue: number)\n\tlocal v = parent:FindFirstChild(name)\n\tif not v then\n\t\tv = Instance.new(\"IntValue\")\n\t\tv.Name = name\n\t\tv.Value = defaultValue or 0\n\t\tv.Parent = parent\n\tend\n\treturn v\nend\n\nlocal function getRng(unit: Model): Random\n\tlocal info = getInfo(unit)\n\tif not info then\n\t\treturn Random.new()\n\tend\n\n\tlocal seedVal = info:FindFirstChild(\"DiceRollSeed\")\n\tif not seedVal then\n\t\tseedVal = Instance.new(\"IntValue\")\n\t\tseedVal.Name = \"DiceRollSeed\"\n\t\tseedVal.Value = math.random(1, 2_000_000_000)\n\t\tseedVal.Parent = info\n\tend\n\n\tseedVal.Value = (seedVal.Value * 1103515245 + 12345) % 2_000_000_000\n\treturn Random.new(seedVal.Value)\nend\n\nlocal function randFloat(r: Random, a: number, b: number)\n\tif a > b then a, b = b, a end\n\treturn a + (b - a) * r:NextNumber()\nend\n\nlocal function rollTargets(unit: Model)\n\tlocal upg = getAttackUpgrade(unit)\n\tlocal entry = DiceRoll[upg]\n\tif not entry then return end\n\n\tlocal r = getRng(unit)\n\n\tlocal dmg = math.floor(randFloat(r, entry.Damage[1], entry.Damage[2]) + 0.5)\n\tlocal spa = randFloat(r, entry.SPA[1], entry.SPA[2])\n\tlocal range = randFloat(r, entry.Range[1], entry.Range[2])\n\n\treturn dmg, spa, range\nend\n\nlocal function applyDiceRoll(unit: Model)\n\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\tlocal stats = getStats(unit)\n\tif not stats then return end\n\n\tMethods.RemoveFixedBuff(unit, TAG_ROLL)\n\tMethods.RecalculateStats(unit)\n\n\tlocal curDmg = 0\n\tlocal curSpa = stats and stats:FindFirstChild(\"SPA\") and stats.SPA.Value or 0\n\tlocal curRange = 0\n\n\tlocal tgtDmg, tgtSpa, tgtRange = rollTargets(unit)\n\tif not tgtDmg then return end\n\n\ttgtSpa = math.max(0.1, tgtSpa)\n\ttgtRange = math.max(0, tgtRange)\n\n\tMethods.AddFixedBuff(unit, TAG_ROLL, {\n\t\tDamage = (tgtDmg - curDmg),\n\t\tSPA = (tgtSpa - curSpa),\n\t\tRange = (tgtRange - curRange),\n\t})\nend\n\n\nreturn {\n\tCanTrade = true, DisplayName = \"Nagu\",\n\tRarity = \"Secret\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Steel,\n\tPlacementLimit = 2,\n\n\tPassives = {\n\t\tOnPlaced = function(ThisUnit: Model)\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\tThisUnit:SetAttribute(ATTACK_UPG_ATTR, 0)\n\t\t\tThisUnit:SetAttribute(\"DiceRoll_LastTier\", 0)\n\n\t\t\tapplyDiceRoll(ThisUnit, 0)\n\n\t\t\tMethods.RecalculateStats(ThisUnit)\n\t\tend,\n\n\t\tOnUpgrade = function(ThisUnit: Model)\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\tlocal upg = getAttackUpgrade(ThisUnit)\n\t\t\tsetAttackUpgrade(ThisUnit, upg + 1)\n\n\t\t\tapplyDiceRoll(ThisUnit) \n\n\t\t\tMethods.RecalculateStats(ThisUnit)\n\t\tend,\n\t},\n\n\tPassivesData = {\n\t\t[1] = {\n\t\t\tTitle = \"Dice Roll\",\n\t\t\tDescription = \"Every attack rolls random Damage/SPA/Range within the current upgrade thresholds. Damage is scaled by +50% per upgrade (up to +150% at max).\",\n\t\t},\n\t},\n\n\t[0] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 16, StatusList = {\"Bloodloss\"},\n\t\tCost = 2500, Damage = 40000, Range = (20+35)/2, SPA = (5.5+8)/2,\n\t},\n\n\t[