n ensureDamageBuff(unit: Model)\n\tlocal info, _, buffs = getInfoStatsBuffs(unit)\n\tif not info then\n\t\treturn nil\n\tend\n\n\tif not buffs then\n\t\tbuffs = Instance.new(\"Folder\")\n\t\tbuffs.Name = \"Buffs\"\n\t\tbuffs.Parent = info\n\tend\n\n\tlocal dmgBuff = buffs:FindFirstChild(\"Damage\")\n\tif not dmgBuff then\n\t\tdmgBuff = Instance.new(\"NumberValue\")\n\t\tdmgBuff.Name = \"Damage\"\n\t\tdmgBuff.Value = 0\n\t\tdmgBuff.Parent = buffs\n\n\tend\n\n\tif not dmgBuff:IsA(\"NumberValue\") then\n\t\treturn nil\n\tend\n\n\treturn dmgBuff\nend\n\nreturn {\n\tCanTrade = true, DisplayName = \"Motomoto Bunny [Thin]\",\n\tRarity = \"Mythic\",\n\tPlaceType = \"Ground\",\n\tType = TypeList.Fighting,\n\tPlacementLimit = 3,\n\n\tPassives = {\n\t\tOnSpawn = function(ThisUnit: Model)\n\n\t\t\tif not RunService:IsServer() then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tif not ThisUnit or not ThisUnit.Parent then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tif ThisUnit:GetAttribute(\"AtkStackInit\") then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tThisUnit:SetAttribute(\"AtkStackInit\", true)\n\t\t\tThisUnit:SetAttribute(\"AtkStacks_LastApplied\", 0)\n\t\t\tThisUnit:SetAttribute(\"AtkStacks_Token\", 0)\n\n\t\t\t-- sanity: ensure dmg buff exists at spawn\n\t\t\tlocal dmgBuff = ensureDamageBuff(ThisUnit)\n\t\tend,\n\n\t\tOnAttack = function(Player, ThisUnit: Model)\n\t\t\tif not RunService:IsServer() then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tif not ThisUnit or not ThisUnit.Parent then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\n\t\t\tlocal dmgBuff = ensureDamageBuff(ThisUnit)\n\t\t\tif not dmgBuff then\n\t\t\t\treturn\n\t\t\tend\n\n\t\t\tlocal lastApplied = ThisUnit:GetAttribute(\"AtkStacks_LastApplied\") or 0\n\t\t\tlocal desired = math.min(CAP, lastApplied + STEP)\n\n\t\t\tif desired ~= lastApplied then\n\t\t\t\tdmgBuff.Value -= lastApplied\n\t\t\t\tdmgBuff.Value += desired\n\t\t\t\tThisUnit:SetAttribute(\"AtkStacks_LastApplied\", desired)\n\n\t\t\t\tMethods.RecalculateStats(ThisUnit)\n\t\t\telse\n\t\t\tend\n\n\t\t\tlocal token = (ThisUnit:GetAttribute(\"AtkStacks_Token\") or 0) + 1\n\t\t\tThisUnit:SetAttribute(\"AtkStacks_Token\", token)\n\n\t\t\ttask.delay(IDLE_RESET, function()\n\t\t\t\tif not ThisUnit or not ThisUnit.Parent then\n\t\t\t\t\treturn\n\t\t\t\tend\n\n\t\t\t\tlocal currentToken = ThisUnit:GetAttribute(\"AtkStacks_Token\") or 0\n\t\t\t\tif currentToken ~= token then\n\t\t\t\t\treturn\n\t\t\t\tend\n\n\t\t\t\tlocal currentApplied = ThisUnit:GetAttribute(\"AtkStacks_LastApplied\") or 0\n\n\t\t\t\tif currentApplied ~= 0 then\n\t\t\t\t\tlocal dmgBuff2 = ensureDamageBuff(ThisUnit)\n\t\t\t\t\tif not dmgBuff2 then\n\t\t\t\t\t\treturn\n\t\t\t\t\tend\n\n\t\t\t\t\tdmgBuff2.Value -= currentApplied\n\t\t\t\t\tThisUnit:SetAttribute(\"AtkStacks_LastApplied\", 0)\n\t\t\t\t\tMethods.RecalculateStats(ThisUnit)\n\t\t\t\tend\n\t\t\tend)\n\t\tend,\n\t},\n\n\n\tPassivesData = {\n\t\t[1] = {\n\t\t\tTitle = \"Quick Metabolism\",\n\t\t\tDescription = \"his unit will get +10 % damage every attack (cap of 100% damage). If this unit stops attacking for 15 seconds, the damage boost will be lost.\"\n\t\t}\n\t},\n\n\t[0] = {\n\t\tTargetType = \"Ground\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 8, StatusList = {},\n\t\tCost = 1000, Damage = 8000, Range = 35, SPA = 6,\n\t},\n\n\t[1] = {\n\t\tTargetType = \"Ground\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 8, StatusList = {},\n\t\tCost = 2800, Damage = 16000, Range = 40, SPA = 6,\n\t},\n\n\t[2] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Weapon Combination\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 10, StatusList = {},\n\t\tCost = 5600, Damage = 22500, Range = 45, SPA = 5,\n\t},\n\n\t[3] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Weapon Combination\", Ability = \"\", AOEType = \"Line\", AOESize = Vector3.one * 10, StatusList = {},\n\