n\tlocal aHRP = a:FindFirstChild(\"HumanoidRootPart\")\n\tlocal bHRP = b:FindFirstChild(\"HumanoidRootPart\")\n\tif not aHRP or not bHRP then return false end\n\treturn (aHRP.Position - bHRP.Position).Magnitude <= range\nend\n\nlocal function isQualifyingUnit(u: Model): boolean\n\tlocal rarity = getUnitRarity(u)\n\tif not rarity then return false end\n\treturn QUALIFYING[tostring(rarity)] == true\nend\n\nreturn {\n\tCanTrade = true, DisplayName = \"S-Rank Bomber\",\n\tRarity = \"Mythic\",\n\tPlaceType = \"Hill\",\n\tType = TypeList.Fire,\n\tPlacementLimit = 5,\n\t\n\tTagTeamWith = \"BaekEvo\",\n\tTagTeamRange = 20,\n\tTagTeamEffect = {\n\t\tOnActivate = function(ThisUnit: Model, partnerUnit: Model?)\n\t\t\tif not RunService:IsServer() then return end\n\t\t\tif not ThisUnit or ThisUnit.Parent ~= workspace.Units then return end\n\n\t\t\tlocal Methods = require(ServerStorage.Server.Utility.Methods)\n\t\t\tlocal _, stats, buffs = getInfoStatsBuffs(ThisUnit)\n\t\t\tif not stats or not buffs then return end\n\n\t\t\tThisUnit:SetAttribute(ACTIVE_ATTR, true)\n\n\t\t\tlocal dmgBuff = buffs:FindFirstChild(BUFF_VALUE_NAME)\n\t\t\tif not dmgBuff then\n\t\t\t\tdmgBuff = Instance.new(\"NumberValue\")\n\t\t\t\tdmgBuff.Name = BUFF_VALUE_NAME\n\t\t\t\tdmgBuff.Value = 0\n\t\t\t\tdmgBuff.Parent = buffs\n\t\t\tend\n\n\t\t\tif ThisUnit:GetAttribute(STARTED_ATTR) then\n\t\t\t\treturn\n\t\t\tend\n\t\t\tThisUnit:SetAttribute(STARTED_ATTR, true)\n\t\t\t\n\t\t\tlocal function cleanup()\n\t\t\t\tThisUnit:SetAttribute(ACTIVE_ATTR, false)\n\n\t\t\t\tlocal lastApplied = ThisUnit:GetAttribute(LAST_ATTR) or 0\n\t\t\t\tif dmgBuff and dmgBuff.Parent then\n\t\t\t\t\tdmgBuff.Value -= lastApplied\n\t\t\t\t\tif math.abs(dmgBuff.Value) < 1e-9 then\n\t\t\t\t\t\tdmgBuff:Destroy()\n\t\t\t\t\tend\n\t\t\t\tend\n\n\t\t\t\tThisUnit:SetAttribute(LAST_ATTR, 0)\n\t\t\t\tThisUnit:SetAttribute(STARTED_ATTR, false)\n\n\t\t\t\tif ThisUnit.Parent then\n\t\t\t\t\tMethods.RecalculateStats(ThisUnit)\n\t\t\t\tend\n\t\t\tend\n\n\t\t\tlocal function recompute()\n\t\t\t\tif not ThisUnit or ThisUnit.Parent ~= workspace.Units then return end\n\t\t\t\tif not ThisUnit:GetAttribute(ACTIVE_ATTR) then return end\n\n\t\t\t\tlocal range = 20\n\t\t\t\tlocal myPos = ThisUnit:GetPivot().Position\n\n\t\t\t\tlocal count = 0\n\t\t\t\tfor _, u in ipairs(workspace.Units:GetChildren()) do\n\t\t\t\t\tif u ~= ThisUnit and u.Parent == workspace.Units then\n\t\t\t\t\t\tlocal dist = (myPos - u:GetPivot().Position).Magnitude\n\t\t\t\t\t\tif dist <= range and isQualifyingUnit(u) then\n\t\t\t\t\t\t\tcount += 1\n\t\t\t\t\t\tend\n\t\t\t\t\tend\n\t\t\t\tend\n\n\t\t\t\tlocal desired = (count * DMG_PER_UNIT) / 100\n\t\t\t\tlocal lastApplied = ThisUnit:GetAttribute(LAST_ATTR) or 0\n\n\t\t\t\tif desired ~= lastApplied then\n\t\t\t\t\tdmgBuff.Value = (dmgBuff.Value - lastApplied) + desired\n\t\t\t\t\tThisUnit:SetAttribute(LAST_ATTR, desired)\n\t\t\t\t\tMethods.RecalculateStats(ThisUnit)\n\t\t\t\tend\n\t\t\tend\n\n\t\t\trecompute()\n\t\t\t\n\t\t\tpartnerUnit.Destroying:Once(cleanup)\n\t\t\tThisUnit.Destroying:Once(cleanup)\n\n\t\t\ttask.spawn(function()\n\t\t\t\twhile ThisUnit and ThisUnit.Parent == workspace.Units do\n\t\t\t\t\tif not ThisUnit:GetAttribute(ACTIVE_ATTR) then\n\t\t\t\t\t\tbreak\n\t\t\t\t\tend\n\n\t\t\t\t\trecompute()\n\t\t\t\t\tTaskLibrary.wait(TICK)\n\t\t\t\tend\n\t\t\tend)\n\t\tend,\n\t},\n\n\tTagTeamData = {\n\t\tTitle = \"Guild Rivalry\",\n\t\tDescription = \"For every Rare/Epic/Legendary unit placed in this unit's tag team range, this unit gains +25% damage per unit.\",\n\t},\n\n\t[0] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 16, StatusList = {\"Burn\"},\n\t\tCost = 1100, Damage = 2700, Range = 24, SPA = 5,\n\t},\n\t\n\t[1] = {\n\t\tTargetType = \"Hybrid\",\n\t\tModuleName = \"Attack1\", Ability = \"\", AOEType = \"Circle\", AOESize = Vector3.one * 16, StatusList = {\"Burn\"},\n\t\tCost = 2800, Damage = 6600, Range =